"use client";

import React, { useState } from "react";
import { motion } from "framer-motion";
import {
  MapPin,
  Star,
  Clock,
  Building2,
  Users,
  Search,
  Filter,
  Phone,
  Navigation,
  Film,
  Calendar,
  ChevronRight,
  Award,
  Zap,
  Shield,
  Car,
  Coffee,
  Wifi,
  ShoppingBag,
  Utensils,
} from "lucide-react";
import Link from "next/link";
import Image from "next/image";
import { Button } from "@/components/ui/Button";
import { Input } from "@/components/ui/Input";

interface Cinema {
  id: string;
  name: string;
  brand: string;
  city: string;
  district: string;
  address: string;
  phone: string;
  rating: number;
  reviewCount: number;
  distance: number;
  image: string;
  features: string[];
  hallCount: number;
  totalCapacity: number;
  movieCount: number;
  sessionCount: number;
  isPartner: boolean;
  isPremium: boolean;
  facilities: string[];
}

const CinemasPage = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCity, setSelectedCity] = useState("all");
  const [selectedBrand, setSelectedBrand] = useState("all");
  const [sortBy, setSortBy] = useState("distance");

  const [cinemas] = useState<Cinema[]>([
    {
      id: "1",
      name: "CinemaMaximum Zorlu",
      brand: "CinemaMaximum",
      city: "İstanbul",
      district: "Beşiktaş",
      address: "Zorlu Center, Levazım Mah. Koru Sok. No:2",
      phone: "0212 924 0000",
      rating: 4.8,
      reviewCount: 2847,
      distance: 2.3,
      image:
        "data:image/jpeg;base64,",
      features: ["IMAX", "4DX", "VIP", "Premium Sound"],
      hallCount: 12,
      totalCapacity: 2400,
      movieCount: 15,
      sessionCount: 48,
      isPartner: true,
      isPremium: true,
      facilities: ["Otopark", "Restoran", "Kafe", "Wi-Fi"],
    },
    {
      id: "2",
      name: "Cinebonus Akasya",
      brand: "Cinebonus",
      city: "İstanbul",
      district: "Acıbadem",
      address: "Akasya AVM, Acıbadem Mah. Çeçen Sok. No:3",
      phone: "0216 571 2000",
      rating: 4.6,
      reviewCount: 1923,
      distance: 5.7,
      image:
        "data:image/jpeg;base64,",
      features: ["Dolby Atmos", "VIP", "Gold Class"],
      hallCount: 10,
      totalCapacity: 1800,
      movieCount: 12,
      sessionCount: 36,
      isPartner: true,
      isPremium: false,
      facilities: ["Otopark", "Kafe", "Wi-Fi", "Alışveriş"],
    },
    {
      id: "3",
      name: "CGV Mars",
      brand: "CGV",
      city: "İstanbul",
      district: "Bakırköy",
      address: "Capacity AVM, Ataköy 2-5-6. Kısım Mah.",
      phone: "0212 465 8000",
      rating: 4.4,
      reviewCount: 1567,
      distance: 8.2,
      image:
        "data:image/jpeg;base64,",
      features: ["4DX", "Screen X", "Premium"],
      hallCount: 8,
      totalCapacity: 1400,
      movieCount: 10,
      sessionCount: 32,
      isPartner: false,
      isPremium: true,
      facilities: ["Otopark", "Restoran", "Wi-Fi"],
    },
    {
      id: "4",
      name: "CinemaMaximum Ankara",
      brand: "CinemaMaximum",
      city: "Ankara",
      district: "Çankaya",
      address: "Panora AVM, Hoşdere Cad. No:176",
      phone: "0312 447 0000",
      rating: 4.7,
      reviewCount: 1834,
      distance: 450.5,
      image: "/placeholder-cinema.jpg",
      features: ["IMAX", "VIP", "Dolby Atmos"],
      hallCount: 9,
      totalCapacity: 1650,
      movieCount: 13,
      sessionCount: 40,
      isPartner: true,
      isPremium: true,
      facilities: ["Otopark", "Kafe", "Wi-Fi", "Alışveriş"],
    },
    {
      id: "5",
      name: "Cinebonus İzmir",
      brand: "Cinebonus",
      city: "İzmir",
      district: "Bornova",
      address: "Forum Bornova AVM, Kazımdirik Mah.",
      phone: "0232 462 0000",
      rating: 4.5,
      reviewCount: 1456,
      distance: 560.8,
      image: "/placeholder-cinema.jpg",
      features: ["VIP", "Gold Class", "Premium Sound"],
      hallCount: 7,
      totalCapacity: 1200,
      movieCount: 11,
      sessionCount: 28,
      isPartner: true,
      isPremium: false,
      facilities: ["Otopark", "Restoran", "Wi-Fi"],
    },
    {
      id: "6",
      name: "CinemaMaximum Bursa",
      brand: "CinemaMaximum",
      city: "Bursa",
      district: "Nilüfer",
      address: "Carrefour Bursa AVM, Odunluk Mah.",
      phone: "0224 441 0000",
      rating: 4.3,
      reviewCount: 987,
      distance: 240.3,
      image:
        "data:image/jpeg;base64,",
      features: ["IMAX", "VIP", "Premium"],
      hallCount: 6,
      totalCapacity: 1100,
      movieCount: 9,
      sessionCount: 24,
      isPartner: true,
      isPremium: true,
      facilities: ["Otopark", "Kafe", "Wi-Fi"],
    },
  ]);

  const cities = [
    "all",
    ...Array.from(new Set(cinemas.map((cinema) => cinema.city))),
  ];
  const brands = [
    "all",
    ...Array.from(new Set(cinemas.map((cinema) => cinema.brand))),
  ];

  const filteredCinemas = cinemas.filter((cinema) => {
    const matchesSearch =
      cinema.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      cinema.district.toLowerCase().includes(searchTerm.toLowerCase()) ||
      cinema.city.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCity = selectedCity === "all" || cinema.city === selectedCity;
    const matchesBrand =
      selectedBrand === "all" || cinema.brand === selectedBrand;
    return matchesSearch && matchesCity && matchesBrand;
  });

  const sortedCinemas = [...filteredCinemas].sort((a, b) => {
    switch (sortBy) {
      case "rating":
        return b.rating - a.rating;
      case "name":
        return a.name.localeCompare(b.name);
      case "city":
        return a.city.localeCompare(b.city);
      default:
        return a.distance - b.distance;
    }
  });

  const formatDistance = (distance: number) => {
    if (distance < 1) {
      return `${Math.round(distance * 1000)}m`;
    }
    return `${distance.toFixed(1)}km`;
  };

  const getFacilityIcon = (facility: string) => {
    switch (facility) {
      case "Otopark":
        return <Car className="h-4 w-4" />;
      case "Restoran":
        return <Utensils className="h-4 w-4" />;
      case "Kafe":
        return <Coffee className="h-4 w-4" />;
      case "Wi-Fi":
        return <Wifi className="h-4 w-4" />;
      case "Alışveriş":
        return <ShoppingBag className="h-4 w-4" />;
      default:
        return <Shield className="h-4 w-4" />;
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-purple-900 to-blue-900">
 
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        <div className="absolute -top-40 -right-40 w-80 h-80 bg-purple-500/10 rounded-full blur-3xl"></div>
        <div className="absolute -bottom-40 -left-40 w-80 h-80 bg-blue-500/10 rounded-full blur-3xl"></div>
      </div>

      <div className="relative">
 
        <header className="bg-black/20 backdrop-blur-md border-b border-white/10 sticky top-0 z-50">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="flex items-center justify-between h-16">
              <Link href="/" className="flex items-center space-x-3">
                <Image
                  src="/cinefyLogo.png"
                  alt="CinefyAI Logo"
                  width={40}
                  height={40}
                  className="rounded-lg"
                />
                <span className="text-white text-xl font-bold">CinefyAI</span>
              </Link>

              <nav className="hidden md:flex items-center space-x-8">
                <Link
                  href="/movies"
                  className="text-gray-300 hover:text-white transition-colors">
                  Filmler
                </Link>
                <Link
                  href="/cinemas"
                  className="text-purple-300 hover:text-white transition-colors">
                  Sinemalar
                </Link>
                <Link
                  href="/about"
                  className="text-gray-300 hover:text-white transition-colors">
                  Hakkımızda
                </Link>
              </nav>
            </div>
          </div>
        </header>

       
        <section className="relative py-16 px-4 sm:px-6 lg:px-8">
          <div className="max-w-7xl mx-auto">
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              className="text-center mb-12">
              <h1 className="text-4xl md:text-6xl font-bold text-white mb-6">
                Sinema
                <span className="bg-gradient-to-r from-purple-400 to-blue-400 bg-clip-text text-transparent">
                  {" "}
                  Salonları
                </span>
              </h1>
              <p className="text-xl text-gray-300 max-w-3xl mx-auto">
                Türkiye genelindeki sinema salonlarını keşfedin ve size en yakın
                salonu bulun
              </p>
            </motion.div>

          
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.2 }}
              className="bg-white/10 backdrop-blur-md rounded-2xl p-6 border border-white/20 mb-12">
              <div className="flex flex-col lg:flex-row gap-4">
                <div className="flex-1">
                  <Input
                    placeholder="Sinema adı, şehir veya bölge ara..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    leftIcon={<Search className="h-5 w-5" />}
                    className="bg-white/5 border-white/20 text-white placeholder-gray-400"
                  />
                </div>

                <select
                  value={selectedCity}
                  onChange={(e) => setSelectedCity(e.target.value)}
                  className="px-4 py-2 bg-white/5 border border-white/20 rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-purple-500">
                  <option value="all" className="bg-gray-800">
                    Tüm Şehirler
                  </option>
                  {cities
                    .filter((c) => c !== "all")
                    .map((city) => (
                      <option key={city} value={city} className="bg-gray-800">
                        {city}
                      </option>
                    ))}
                </select>

                <select
                  value={selectedBrand}
                  onChange={(e) => setSelectedBrand(e.target.value)}
                  className="px-4 py-2 bg-white/5 border border-white/20 rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-purple-500">
                  <option value="all" className="bg-gray-800">
                    Tüm Markalar
                  </option>
                  {brands
                    .filter((b) => b !== "all")
                    .map((brand) => (
                      <option key={brand} value={brand} className="bg-gray-800">
                        {brand}
                      </option>
                    ))}
                </select>

                <select
                  value={sortBy}
                  onChange={(e) => setSortBy(e.target.value)}
                  className="px-4 py-2 bg-white/5 border border-white/20 rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-purple-500">
                  <option value="distance" className="bg-gray-800">
                    Uzaklık
                  </option>
                  <option value="rating" className="bg-gray-800">
                    Puan
                  </option>
                  <option value="name" className="bg-gray-800">
                    İsim
                  </option>
                  <option value="city" className="bg-gray-800">
                    Şehir
                  </option>
                </select>
              </div>
            </motion.div>
          </div>
        </section>

 
        <section className="pb-16 px-4 sm:px-6 lg:px-8">
          <div className="max-w-7xl mx-auto">
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
              {sortedCinemas.map((cinema, index) => (
                <motion.div
                  key={cinema.id}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: index * 0.1 }}
                  className="bg-white/10 backdrop-blur-md rounded-2xl overflow-hidden border border-white/20 hover:bg-white/15 transition-all duration-300 group">
             
                  <div className="relative h-48 overflow-hidden">
                    <img
                      src={cinema.image}
                      alt={cinema.name}
                      className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-300"
                    />
                    <div className="absolute inset-0 bg-gradient-to-t from-black/80 to-transparent"></div>
 
                    <div className="absolute top-4 left-4 flex gap-2">
                      {cinema.isPremium && (
                        <span className="bg-yellow-500 text-black px-2 py-1 rounded-lg text-xs font-medium flex items-center">
                          <Award className="h-3 w-3 mr-1" />
                          PREMIUM
                        </span>
                      )}
                      {cinema.isPartner && (
                        <span className="bg-green-500 text-white px-2 py-1 rounded-lg text-xs font-medium flex items-center">
                          <Zap className="h-3 w-3 mr-1" />
                          PARTNER
                        </span>
                      )}
                    </div>

                     
                    <div className="absolute top-4 right-4">
                      <span className="bg-black/50 backdrop-blur-sm text-white px-3 py-1 rounded-full text-sm font-medium">
                        {formatDistance(cinema.distance)}
                      </span>
                    </div>

                
                    <div className="absolute bottom-4 left-4">
                      <span className="bg-white/90 text-gray-900 px-3 py-1 rounded-lg text-sm font-bold">
                        {cinema.brand}
                      </span>
                    </div>
                  </div>

           
                  <div className="p-6">
                    <div className="mb-4">
                      <h3 className="text-xl font-bold text-white mb-2">
                        {cinema.name}
                      </h3>
                      <div className="flex items-center space-x-4 text-sm text-gray-300 mb-3">
                        <div className="flex items-center space-x-1">
                          <Star className="h-4 w-4 text-yellow-400 fill-current" />
                          <span>{cinema.rating}</span>
                          <span className="text-gray-500">
                            ({cinema.reviewCount})
                          </span>
                        </div>
                        <div className="flex items-center space-x-1">
                          <MapPin className="h-4 w-4 text-blue-400" />
                          <span>
                            {cinema.city}, {cinema.district}
                          </span>
                        </div>
                      </div>
                      <p className="text-gray-400 text-sm">{cinema.address}</p>
                    </div>

                
                    <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-4">
                      <div className="bg-white/5 rounded-lg p-3 text-center">
                        <Building2 className="h-5 w-5 text-purple-400 mx-auto mb-1" />
                        <div className="text-white font-medium">
                          {cinema.hallCount}
                        </div>
                        <div className="text-xs text-gray-400">Salon</div>
                      </div>
                      <div className="bg-white/5 rounded-lg p-3 text-center">
                        <Users className="h-5 w-5 text-blue-400 mx-auto mb-1" />
                        <div className="text-white font-medium">
                          {cinema.totalCapacity}
                        </div>
                        <div className="text-xs text-gray-400">Kapasite</div>
                      </div>
                      <div className="bg-white/5 rounded-lg p-3 text-center">
                        <Film className="h-5 w-5 text-green-400 mx-auto mb-1" />
                        <div className="text-white font-medium">
                          {cinema.movieCount}
                        </div>
                        <div className="text-xs text-gray-400">Film</div>
                      </div>
                      <div className="bg-white/5 rounded-lg p-3 text-center">
                        <Calendar className="h-5 w-5 text-orange-400 mx-auto mb-1" />
                        <div className="text-white font-medium">
                          {cinema.sessionCount}
                        </div>
                        <div className="text-xs text-gray-400">Seans</div>
                      </div>
                    </div>

                  
                    <div className="mb-4">
                      <h5 className="text-white font-medium mb-2">
                        Özellikler
                      </h5>
                      <div className="flex flex-wrap gap-1">
                        {cinema.features.map((feature) => (
                          <span
                            key={feature}
                            className="px-2 py-1 bg-purple-500/20 text-purple-300 rounded-lg text-xs">
                            {feature}
                          </span>
                        ))}
                      </div>
                    </div>
 
                    <div className="mb-4">
                      <h5 className="text-white font-medium mb-2">Olanaklar</h5>
                      <div className="flex flex-wrap gap-2">
                        {cinema.facilities.map((facility) => (
                          <div
                            key={facility}
                            className="flex items-center space-x-1 bg-white/5 rounded-lg px-2 py-1">
                            {getFacilityIcon(facility)}
                            <span className="text-gray-300 text-xs">
                              {facility}
                            </span>
                          </div>
                        ))}
                      </div>
                    </div>

                  
                    <div className="flex flex-col sm:flex-row gap-3">
                      <Link href={`/cinema/${cinema.id}`} className="flex-1">
                        <Button className="w-full bg-purple-600 hover:bg-purple-700">
                          <Building2 className="h-4 w-4 mr-2" />
                          Filmleri Görüntüle
                        </Button>
                      </Link>

                      <div className="flex space-x-2">
                        <Button
                          variant="ghost"
                          size="sm"
                          className="text-white hover:bg-white/10 px-3">
                          <Phone className="h-4 w-4" />
                        </Button>
                        <Button
                          variant="ghost"
                          size="sm"
                          className="text-white hover:bg-white/10 px-3">
                          <Navigation className="h-4 w-4" />
                        </Button>
                      </div>
                    </div>
                  </div>
                </motion.div>
              ))}
            </div>

            {sortedCinemas.length === 0 && (
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                className="text-center py-12">
                <Building2 className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                <h3 className="text-lg font-medium text-white mb-2">
                  Sinema bulunamadı
                </h3>
                <p className="text-gray-400">
                  Arama kriterlerinize uygun sinema bulunamadı.
                </p>
              </motion.div>
            )}
          </div>
        </section>
      </div>
    </div>
  );
};

export default CinemasPage;
